	def get_page_data(self, category_url, html):
		rtn = False
		product_count = 0
		display_count = self.DISPLAY_LIST_COUNT
		
		productSortType_str = 'RECENT'
		productDisplayType_str = 'IMAGE'
				
		soup = bs4.BeautifulSoup(html, 'lxml')
		
		# 조회 물품수
		count_div_list = soup.find_all('div', {'id':'CategoryProducts'})
		for count_div_ctx in count_div_list :
			ul_ctx = count_div_ctx.find('ul')
			if(ul_ctx != None) :
				a_link_list = ul_ctx.find_all('a')
				for a_link_ctx in a_link_list :
					if('class' in a_link_ctx.attrs ) :
						class_list = a_link_ctx.attrs['class']
						match_class_num = 0
						for classname in class_list :
							if(classname == 'N=a:ctt.cat' ) : match_class_num += 1
							if(classname == 'linkAnchor' ) :  match_class_num += 1
							
						if(match_class_num == 2) :
							strong_ctx = a_link_ctx.find('strong')
							if(strong_ctx != None) :
								product_count = int( __UTIL__.get_only_digit( strong_ctx.get_text().strip() ) )
								#__LOG__.Trace( 'TOTAL COUNT : %d ' % product_count )
								break
		
			if( product_count == 0) :
				# 조회 물품수
				count_div_list = count_div_ctx.find_all('span', class_='last_depth')
				for div_ctx in count_div_list :
					split_list = div_ctx.get_text().strip().split('(')
					cate_str = split_list[0].strip()
					strong_list = div_ctx.find_all('span', class_='thm')
					for strong_ctx in strong_list :
						strong_str = strong_ctx.get_text().strip()
						product_count = int( __UTIL__.get_only_digit(strong_str ))
						#__LOG__.Trace( '%s - TOTAL COUNT : %d ' % (cate_str, product_count ) )
			

		# 페이지 URL 생성
		category_url_list = category_url.split('?cp=')
		first_url = category_url_list[0].strip()
		page = 1
		while(True) :
			page_url = '%s?st=POPULAR&free=false&dt=IMAGE&page=%d&size=40' % (first_url , page )

			if(self.PAGE_URL_HASH.get(page_url , -1) == -1) : 
				self.PAGE_URL_HASH[page_url] = 1
				if(config.__DEBUG__) : __LOG__.Trace( page_url )
				rtn = True
			if( (page*display_count) < product_count ) : page += 1
			else : break

		return rtn 	

		
